# インライン画像生成
#
#  デフォルト画像
#  MiniImage.source # => "data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABAQMAAAAl21bKAAAAA1BMVEUAAP+KeNJXAAAACklEQVQI12NgAAAAAgAB4iG8MwAAAABJRU5ErkJggg==" }
#
#  カスタマイズ
#  MiniImage.source(width: 640, height: 480, color: "red", format: :jpg) # => "data:image/jpeg;base64," }
#
require "base64"

class MiniImage
  class << self
    def generate(*args)
      new(*args).to_s
    end
  end

  def initialize(options = {})
    @options = {
      :width  => 1,
      :height => 1,
      :color  => "blue",
      :format => "png",
    }.merge(options)
  end

  def to_s
    "data:#{mime.content_type};base64,#{Base64.strict_encode64(binary)}"
  end

  private

  def binary
    if defined? Magick
      image = Magick::Image.new(*size) { |e| e.background_color = color }
      image.format = ext_name
      image.strip!
      image.to_blob
    else
      `convert -strip -size #{size.join("x")} xc:#{color} #{ext_name}:-`
    end
  end

  def mime
    MiniMime.lookup_by_extension(ext_name)
  end

  def color
    @options[:color].to_s
  end

  def size
    [@options[:width], @options[:height]]
  end

  def ext_name
    @options[:format].to_s
  end
end
