        { key: "チョコレートマン",   medal_params: { message: "チョコレート囲いで勝った",                     method: "raw",  name: "🍫",                type: nil,                            }, if_cond: proc { win_tag.exist?(:"チョコレート囲い")                                                                                                                                        },},
