        { key: "5連勝",              medal_params: { message: "5連勝した",                                    method: "raw",  name: "🍰",                type: nil,                            }, if_cond: proc { streak_stat.count(:win).between?(5, 9)                              },},
        { key: "10連勝",             medal_params: { message: "10連勝した",                                   method: "raw",  name: "💮",                type: nil,                            }, if_cond: proc { streak_stat.count(:win) >= 10                                       },},

