        { key: "入玉勝ちマン",       medal_params: { message: "入玉で勝った",                                 method: "raw",  name: "🏈",                 type: nil,                           }, if_cond: proc { win_tag.exist?(:"入玉")                                                                                                                                                    },},
        { key: "切れ負けマン",       medal_params: { message: "切れ負けが多い",                               method: "raw",  name: "⌛",                 type: nil,                           }, if_cond: proc { (r = lose_ratio_of("TIMEOUT")) && r >= 0.25                                                                                                                                              },},
        { key: "レア戦法マン",       medal_params: { message: "変態戦法の使い手",                             method: "raw",  name: "🍀",                type: nil,                            }, if_cond: proc { user_stat.rarity_stat.minority?                                                                                                                                                         },},
        { key: "切断マン",           medal_params: { message: "切断の使い手",                                 method: "raw",  name: "💩",                type: nil,                            }, if_cond: proc { (r = lose_ratio_of("DISCONNECT")) && r > 0                                                                                                                                               },},
        { key: "角不成マン",         medal_params: { message: "角不成で舐めプした",                           method: "raw",  name: "☠",                 type: nil,                            }, if_cond: proc { all_tag.exist?(:"角不成") }           },
        { key: "飛車不成マン",       medal_params: { message: "飛車不成で舐めプした",                         method: "raw",  name: "💀",                type: nil,                            }, if_cond: proc { all_tag.exist?(:"飛車不成") }           },
        { key: "1手詰じらしマン",    medal_params: { message: "1手詰を焦らして歪んだ優越感に浸った",            method: "raw",  name: "😈",                type: nil,                          }, if_cond: proc { (r = jirasi_ratio) && r > 0 } },
        { key: "絶対投了しないマン", medal_params: { message: "悔しかったので時間切れまで放置した",           method: "raw",  name: "🪳",                type: nil,                            }, if_cond: proc { (r = zettai_toryo_sinai_ratio) && r > 0 } },
        { key: "相手退席待ちマン",   medal_params: { message: "放置に痺れを切らした相手が離席したころを見計らって着手し逆時間切れ勝ちを狙ったが失敗", method: "raw",  name: "🧟",   type: nil, }, if_cond: proc { (r = taisekimachi_ratio) && r > 0 } },
        { key: "大長考マン",         medal_params: { message: "対局放棄と受け取られかねない長考をした",       method: "raw",  name: "🚫",                type: nil,                            }, if_cond: proc { (r = long_think_ratio) && r > 0 } },
        { key: "長考マン",           medal_params: { message: "考えすぎて負けがち",                           method: "raw",  name: "🤯",                type: nil,                            }, if_cond: proc { (r = short_think_ratio) && r > 0.1 } },
        { key: "無気力マン",         medal_params: { message: "無気力な対局をした",                           method: "raw",  name: "🦥",                type: nil,                            }, if_cond: proc { (r = hayai_toryo) && r > 0 } },
        { key: "開幕千日手",         medal_params: { message: "開幕千日手をした",                             method: "raw",  name: "❓",                type: nil                             }, if_cond: proc { (r = start_draw_ratio) && r > 0 } },
        { key: "ただの千日手",       medal_params: { message: "千日手があった",                               method: "raw",  name: "🍌",                type: nil                             }, if_cond: proc { (r = draw_ratio) && r > 0 } },
        { key: "友対マン",           medal_params: { message: "友達対局をした",                               method: "raw",  name: "👬",                 type: nil,                           }, if_cond: proc { xmode_stat.exist?(:"友達") } },
        { key: "指導受けマン",       medal_params: { message: "指導対局を受けた",                             method: "raw",  name: "🔥",                type: nil,                            }, if_cond: proc { xmode_stat.exist?(:"指導") } },
        { key: "運営支えマン",       medal_params: { message: "将棋ウォーズの運営を支える力がある",           method: "raw",  name: "🧙‍♂️",                type: nil                           }, if_cond: proc { fraud_stat.count.positive? } },
