require "rails_helper"

RSpec.describe MiniImage, type: :model do
  it "デフォルト画像" do
    is_asserted_by { MiniImage.generate == "data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABAQMAAAAl21bKAAAAA1BMVEUAAP+KeNJXAAAACklEQVQI12NgAAAAAgAB4iG8MwAAAABJRU5ErkJggg==" }
  end

  it "カスタマイズ" do
    is_asserted_by { MiniImage.generate(width: 640, height: 480, color: "red", format: :jpg) == "data:image/jpeg;base64," }
  end
end
